$(document).ready(function(){
//Get the input data using the post method when Push into mysql is clicked .. we pull it using the id fields of ID, Name and Email respectively...
$("#insert").click(function(){
//Get values of the input fields and store it into the variables.
var id=$("#id").val();
var upadhi=$("#upadhi").val();
var name=$("#name").val();
var location=$("#location").val();
var website=$("#website").val();
 
//use the $.post() method to call insert.php file.. this is the ajax request
$.post('insert.php', {id: id, upadhi: upadhi, name: name, location: location, website: website},
function(data){
$("#message").html(data);
$("#message").hide();
$("#message").fadeIn(1500); //Fade in the data given by the insert.php file
});
return false;
});
});
